import React, { Component } from "react";

class Sort extends Component {
  state = {
    orderBy: 'name',
    orderDir: 'desc'
  };

  handleSortByName = (e) => {
    console.log([e.target]);
    console.log(e.target.attributes[2]);
    this.props.handleSort(this.state);
    this.setState({ orderDir: 'asc' });
  }

  render() {
    const { orderDir } = this.state;
    return (
      <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
        <div className="dropdown">
          <button
            type="button"
            className="btn btn-default dropdown-toggle"
            id="dropdownMenu1"
            data-toggle="dropdown"
            aria-haspopup="true"
          >
            Sort By
            <span className="caret" />
          </button>{" "}
          <ul className="dropdown-menu" aria-labelledby="dropdownMenu1">
            <li>
              <a
                role="button"
                href="##"
                value={orderDir}
                onClick={this.handleSortByName}
              > Name ASC/DESC </a>
            </li>
            <li role="separator" className="divider" href="##"></li>
            <li>
              <a role="button" href="##">
                Level ASC/DESC
              </a>
            </li>
          </ul>
          <span
            className="label label-success label-medium"
            style={{ fontSize: "14px" }}
          >
            NAME - DESC
          </span>
        </div>
      </div >
    );
  }
}

export default Sort;
