import React, { Component } from "react";
import { connect } from "react-redux";
import { addSong } from "../../redux/songs/song-action";

class SongAdd extends Component {
    state = {
        title: "",
        duration: 0,
    };

    handleInputChange = (event) => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
        console.log(this.state);
    };

    onSongSubmit = (event) => {
        event.preventDefault();
        this.props.addSong({
            id: Math.floor(Math.random() * 100),
            ...this.state,
        })
    };

    render() {
        return (
            <div className="ui segment container">
                <form onSubmit={this.onSongSubmit}
                    className="ui form">
                    <div className="field">
                        <input
                            type="text"
                            name="title"
                            placeholder="Title"
                            onChange={this.handleInputChange} />
                        <div className="field" >
                            <input
                                type="text"
                                name="duration"
                                placeholder="Duration"
                                onChange={this.handleInputChange} />
                        </div>
                        <button className="ui right floated primary button primary">Save</button>
                    </div>
                </form>
            </div >
        );
    }
}

const mapStateToProps = () => ({});

const mapDispatchToProps = (dispatch) => ({
    addSong: (song) => dispatch(addSong(song))
})
export default connect(mapStateToProps, mapDispatchToProps)(SongAdd);
