import "./App.css";
import React, { Component } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";
import List from "./components/List";
import items from "./mock/task";

class App extends Component {
  state = {
    items: items,
    isShowForm: false, // 46ss
  };

  // 3ms13ss
  handleToggleForm = () => {
    this.setState({ isShowForm: !this.state.isShowForm });
  };

  render() {
    const { items, isShowForm } = this.state; // 1m18ss

    return (
      <div>
        <Title />
        {/* 3ms13ss */}
        <Control toggleForm={this.handleToggleForm} />

        {          isShowForm ? <Form /> : "" // 1m18ss 
        }

        <List items={items} />
      </div>
    );
  }
}

export default App;
