import React, { Component } from "react";

class Sort extends Component {
  state = {
    orderBy: '',
    orderByUi: '',
    orderDirUi: '',
    orderDirName: 'ASC',
    orderDirLevel: 'ASC',
  };

  // v42xx1
  updateForBoth = (orderBy, orderDir) => {
    //update for app
    this.props.handleSort({
      orderBy: orderBy,
      orderDir: orderDir,
    });

    //update for sort
    this.setState({
      orderByUi: orderBy,
      orderDirUi: orderDir,
      orderBy: orderBy,
      //hơi lúa, but ok
      orderDirName: orderDir === 'ASC' ? 'DESC' : 'ASC',
      orderDirLevel: orderDir === 'ASC' ? 'DESC' : 'ASC',
    })

  }

  handleSortByName = () => {
    const { orderDirName } = this.state;
    this.updateForBoth('NAME', orderDirName);
  }

  handleSortByLevel = () => {
    const { orderDirLevel } = this.state;
    this.updateForBoth('LEVEL', orderDirLevel);
  }

  handleSortByNone = () => {
    this.updateForBoth(null, 'ASC');
  }

  render() {
    const { orderByUi, orderDirUi } = this.state;

    return (
      <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
        <div className="dropdown">
          <button
            type="button"
            className="btn btn-default dropdown-toggle"
            id="dropdownMenu1"
            data-toggle="dropdown"
            aria-haspopup="true"
          >
            Sort By
            <span className="caret" />
          </button>{" "}
          <ul className="dropdown-menu" aria-labelledby="dropdownMenu1">
            <li>
              <a
                href="##"
                onClick={this.handleSortByName}
              > Name ASC/DESC </a>
            </li>
            <li role="separator" className="divider" href="##"></li>
            <li>
              <a
                href="##"
                onClick={this.handleSortByLevel}
              > Level ASC/DESC </a>
            </li>
            <li role="separator" className="divider" href="##"></li>
            <li>
              <a
                href="##"
                onClick={this.handleSortByNone}
              > None </a>
            </li>
          </ul>
          <span
            className="label label-success label-medium"
            style={{ fontSize: "14px" }}
          >
            {
              orderByUi ? (`${orderByUi} - ${orderDirUi}`) : ('')
            }
          </span>
        </div>
      </div >
    );
  }
}

export default Sort;
