import "./App.css";
import React, { Component } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";
import List from "./components/List";
import items from "./mock/task";
import SearchSortHandler from "./control/SearchSortHandler";

class App extends Component {
  state = {
    items: items,
    isShowForm: false,
    strSearch: '',
    orderBy: '',
    orderDir: '',
    itemSelected: null,
  };

  handleToggleForm = () => {
    this.setState({ isShowForm: !this.state.isShowForm });
  };

  handleSearch = (value) => {
    this.setState({ strSearch: value });
  }

  handleSort = (value) => {
    this.setState({
      orderBy: value.orderBy,
      orderDir: value.orderDir
    })
  }

  handleDelete = (id) => {
    const items = this.state.items.filter(item => item.id !== id);
    this.setState({ items: items });
  }

  handleAdd = (formState) => {
    let { v4: idRandom } = require("uuid");
    const { items } = this.state;
    const { taskName, taskLevel, taskId } = formState;

    if (taskId != null) {
      idRandom = taskId;
      items.forEach(item => {
        if (item.id === idRandom) {
          item.name = taskName;
          item.id = (taskLevel);
          return;
        }
        console.log(item);
      });
    }
    else {
      this.state.items.push({
        id: idRandom(),
        name: taskName,
        level: Number(taskLevel),
      });
    }

    this.setState({ items: this.state.items })
  }

  handleEdit = (item) => {
    this.setState({
      itemSelected: item,
      isShowForm: true,
    })
  }

  render() {
    const { items, isShowForm,
      strSearch, orderBy, orderDir, itemSelected } = this.state;

    if (strSearch || orderBy) {
      var listShown = SearchSortHandler(
        items, orderBy, orderDir, strSearch);
    }

    return (
      <div>
        <Title />
        <Control
          toggleForm={this.handleToggleForm}
          isShowForm={isShowForm}
          strSearch={strSearch}
          handleSearch={this.handleSearch}
          handleSort={this.handleSort}
        />
        {
          isShowForm ?
            <Form
              toggleForm={this.handleToggleForm}
              handleAdd={this.handleAdd}
              editingItem={itemSelected}
            /> : ""
        }
        {
          (<List
            items={listShown ? listShown : items}
            handleDelete={this.handleDelete}
            handleEdit={this.handleEdit}
          />)
        }
      </div>
    );
  }
}

export default App;
