import React, { Component } from "react";

class Item extends Component {
  showLevelEle = (level) => {
    let levelEle = null;
    let label = null;
    if (level === 0) {
      levelEle = "Small";
      label = "label label-primary";
    } else if (level === 1) {
      levelEle = "Medium";
      label = "label label-warning";
    } else if (level === 2) {
      levelEle = "High";
      label = "label label-info";
    }
    return <span className={label}>{levelEle}</span>;
  };

  handleDelete = (id) => {
    console.log(id);
  }

  render() {
    const { idx, item, key } = this.props;
    console.log(key);
    return (
      <tr>
        <td className="text-center">{idx}</td>
        <td>{item.name}</td>
        <td className="text-center">
          {this.showLevelEle(item.level)}
        </td>
        <td>
          <button type="button" className="btn btn-warning">
            Edit
          </button>
          <button
            type="button"
            className="btn btn-danger"
            onClick={() => this.handleDelete(item.id)}//v43xx1
          >
            Delete
          </button>
        </td>
      </tr>
    );
  }
}

export default Item;
