import "./App.css";
import React, { Component } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";

//src1,
class App extends Component {
  render() {
    return (
      <div className="container">
        {/* 1m09ss */}
        <Title />

        {/*4ms16ss */}
        <Control />
        <br />

        {/* FORM : START */}
        <Form />
        <br />
        {/* FORM : END */}

        {/* LIST : START */}
        <div className="panel panel-success">
          <div className="panel-heading">List Task</div>
          <table className="table table-hover">
            <thead>
              <tr>
                <th style={{ width: "10%" }} className="text-center">
                  #
                </th>
                <th>Task</th>
                <th style={{ width: "20%" }} className="text-center">
                  Level
                </th>
                <th style={{ width: "20%" }}>Action</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td className="text-center">1</td>
                <td>
                  Lorem ipsum dolor, sit amet consectetur adipisicing elit.
                </td>
                <td className="text-center">
                  <span className="label label-danger">High</span>
                </td>
                <td>
                  <button type="button" className="btn btn-warning">
                    Edit
                  </button>
                  <button type="button" className="btn btn-danger">
                    Delete
                  </button>
                </td>
              </tr>
              <tr>
                <td className="text-center">2</td>
                <td>
                  Lorem ipsum dolor, sit amet consectetur adipisicing elit 222.
                </td>
                <td className="text-center">
                  <span className="label label-default">Small</span>
                </td>
                <td>
                  <button type="button" className="btn btn-warning">
                    Edit
                  </button>
                  <button type="button" className="btn btn-danger">
                    Delete
                  </button>
                </td>
              </tr>
              <tr>
                <td className="text-center">3</td>
                <td>
                  Lorem ipsum dolor, sit amet consectetur adipisicing elit 333.
                </td>
                <td className="text-center">
                  <span className="label label-default">Medium</span>
                </td>
                <td>
                  <button type="button" className="btn btn-warning">
                    Edit
                  </button>
                  <button type="button" className="btn btn-danger">
                    Delete
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
        {/* LIST : END */}
      </div>
    );
  }
}

