import React, { Component } from "react";

class Search extends Component {
  state = {
    strSearch: '', //6ms16ss
  }
  handleSearch = () => {
    // console.log(this.state.strSearch); //9ms13ss  

    //handle logic, 2ms32ss
    console.log(this.props);
    this.props.handleSearch(this.state.strSearch);

    //clear input value, 11ms47ss
    this.setState({ strSearch: '' });
  }

  // 7ms16ss
  handleChange = (e) => {
    // console.log(e.target.value); 
    this.setState({ strSearch: e.target.value });
  }

  render() {

    return (
      <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Search for..."
            value={this.state.strSearch} //11ms47ss
            onChange={this.handleChange} //7ms16ss
          />
          <span className="input-group-btn">
            <button
              type="button"
              className="btn btn-info"
              onClick={this.handleSearch} //9ms13ss
            >
              Go!
            </button>
          </span>
        </div>
      </div>
    );
  }
}

export default Search;
