import React, { Component } from "react";

class Sort extends Component {
  state = {
    orderBy: '',
    orderDir: 'ASC'
  };

  handleSortByName = (e) => {
    this.props.handleSort({
      orderBy: e.target.type,
      orderDir: this.state.orderDir,
    });

    this.setState({
      orderBy: e.target.type,
      orderDir: this.state.orderDir === 'ASC' ? 'DESC' : 'ASC'
    })
  }

  handleSortByLevel = () => {
    this.props.handleSort;
  }

  render() {
    const { orderDir, orderBy } = this.state;
    console.log(this.state);

    return (
      <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
        <div className="dropdown">
          <button
            type="button"
            className="btn btn-default dropdown-toggle"
            id="dropdownMenu1"
            data-toggle="dropdown"
            aria-haspopup="true"
          >
            Sort By
            <span className="caret" />
          </button>{" "}
          <ul className="dropdown-menu" aria-labelledby="dropdownMenu1">
            <li>
              <a
                role="button"
                href="##"
                type="NAME"
                onClick={this.handleSortByName}
              > Name ASC/DESC </a>
            </li>
            <li role="separator" className="divider" href="##"></li>
            <li>
              <a
                role="button"
                href="##"
                onClick={this.handleSortByLevel}
              > Level ASC/DESC </a>
            </li>
          </ul>
          <span
            className="label label-success label-medium"
            style={{ fontSize: "14px" }}
          >
            {
              orderBy ? (`${orderBy} - ${orderDir}`) : ''
              // orderBy - orderDir // lỗi, v40xx1
            }
          </span>
        </div>
      </div >
    );
  }
}

export default Sort;
