import React, { Component, createRef } from "react";

class Search extends Component {
  constructor(props) {
    super(props);
    this.fieldFocus = createRef();
  }

  state = {
    strSearch: '',
  }

  handleSearch = () => {
    this.props.handleSearch(this.state.strSearch);
    // this.setState({ strSearch: "" }); //v40xx4
    this.fieldFocus.current.focus();
    this.fieldFocus.current.required = true;
    console.log(this.fieldFocus);
  }

  //v40xx4
  handleKeyPress = (event) => {
    if (event.key === "Enter") {
      this.props.handleSearch(this.state.strSearch);
    }
  };

  handleChange = (e) => {
    this.setState({ strSearch: e.target.value });
  }

  render() {
    const { strSearch } = this.state;
    if (strSearch === '' && this.fieldFocus.current) {
      console.log(4567);
      // this.handleSearch();
    }

    return (
      <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Search for..."
            ref={this.fieldFocus}
            value={this.state.strSearch}
            onKeyPress={this.handleKeyPress} //v40xx4
            onChange={this.handleChange}
          />
          <span className="input-group-btn">
            <button
              type="button"
              className="btn btn-info"
              onClick={this.handleSearch}
            >Go!
            </button>
          </span>
        </div>
      </div>
    );
  }
}

export default Search;
