import "./App.css";
import React, { Component } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";
import List from "./components/List";
import items from "./mock/task";

class App extends Component {
  state = {
    items: items,
    isShowForm: false,
    strSearch: '',
  };

  handleToggleForm = () => {
    this.setState({ isShowForm: !this.state.isShowForm });
  };

  handleSearch = (value) => {
    this.setState({ strSearch: value });
  }

  render() {
    const { items, isShowForm, strSearch } = this.state;

    // v38xx2
    let listShown = null;
    if (strSearch) {
      //converting into lower case
      let searchLower = strSearch.toLowerCase();
      let searchedItem = null;
      let searchToNum = null; //converting into lower case

      //converting lowerSearch to number type if existing
      if ('small'.includes(searchLower)) { searchToNum = 0; }
      else if ('medium'.includes(searchLower)) { searchToNum = 1; }
      else if ('high'.includes(searchLower)) { searchToNum = 2; }

      //filtering search by...
      searchedItem = items.filter(item => {
        // task name
        if (item.name.toLowerCase().includes(
          searchLower)) { return item; }

        // level
        else if (item.level === searchToNum) { return item }

        return null;
      })
      listShown = <List items={searchedItem} />
    }
    else {
      listShown = <List items={items} />
    }

    return (
      <div>
        <Title />
        <Control
          toggleForm={this.handleToggleForm}
          isShowForm={isShowForm}
          strSearch={strSearch}
          handleSearch={this.handleSearch}
        />
        {
          isShowForm ?
            <Form toggleForm={this.handleToggleForm} /> : ""
        }

        {/* v38xx2 */}
        {listShown}
        {/* <List items={items} /> */}
      </div>
    );
  }
}

export default App;
