//src6, 1ms58ss
import React, { Component } from 'react';

class StudyForm extends Component {
  // 3ms30ss
  state = {
    fullname: '',
    course: 'React'
  }

  handleChange = (event) => {
    //3ms56ss, 4ms43ss
    // this.setState({ fullname: event.target.value });

    this.setState({ course: event.target.value });
  }

  handleSubmit = (event) => {
    event.preventDefault();

    // 3ms56ss, 5ms41ss
    // alert('A name was submitted: ' + this.state.fullname);
    alert('A course was submitted: ' + this.state.course);
  }

  render() {
    return (
      <div className="row" style={{ margin: '20px 8px' }}>
        <div className="panel panel-info">
          <div className="panel-heading">
            <h3 className="panel-title">Study Form</h3>
          </div>

          <div className="panel-body">
            <form
              action="#" method="post"
              onSubmit={this.handleSubmit} //5ms41ss
            >
              <legend>Form Register</legend>

              <div className="form-group">
                <label htmlFor="">Họ Tên</label>
                <input
                  className="form-control"
                  id="" placeholder="Fullname"

                  // 4ms43ss
                  type="text"
                  value={this.state.fullname}
                  onChange={this.handleChange}
                />
              </div>

              <div className="form-group">
                <label htmlFor="">Khóa học</label>
                <select
                   id='input'
                  className="form-control" required="required"
                  onChange={this.handleChange}
                >
                  <option value="Angular">Angular</option>
                  <option selected value="React">React</option>
                  <option value="Nodejs">Nodejs</option>
                  <option value="Php">Php</option>
                </select>
              </div>

              <div className="form-group">
                <label>Đối tượng</label>
                <div className='radio'>
                  <label>
                    <input type='radio' name='dsd' id='input' defaultValue /> Học viên cũ
                  </label>
                </div>
                <div className='radio'>
                  <label>
                    <input type='radio' name='dsd' id='input' defaultValue /> Học sinh - sinh viên
                  </label>
                </div>
                <div className='radio'>
                  <label>
                    <input type='radio' name='dsd' id='input' defaultValue /> Người khuyết tật
                  </label>
                </div>
              </div>
              <button type="submit" className="btn btn-primary">Submit</button>
            </form>
          </div>
        </div>
      </div>
    )
  }
};

export default StudyForm;
