import React, { Component } from "react";

class Form extends Component {
  state = {
    taskName: '',
    taskLevel: 0,
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    this.props.handleAdd(this.state);

    this.setState({
      taskName: '',
      taskLevel: 0,
    });
  }

  handleUpdate = () => {
    this.props.handleUpdate(this.state);
  }

  render() {
    let { taskName, taskLevel } = this.state;
    let editingItem = this.props.editingItem;
    let name = null;
    let editingLevel = null;

    // 2ms27ss
    if (editingItem.id) {
      // 1m02ss
      name = editingItem.name;
      editingLevel = editingItem.level;
    }

    return (
      <>
        <div className="row">
          <div className="col-md-offset-7 col-md-5">
            {
              <form
                action="true" method="post" className="form-inline"
                onSubmit={this.handleSubmit}
              >
                <div className="form-group">
                  <label htmlFor="true" className="sr-only">
                    label
                  </label>
                  <input
                    type="text"
                    placeholder="Name"
                    className="form-control"
                    name='taskName'
                    value={taskName}
                    defaultValue={name}//1m02ss
                    onChange={this.handleChange}
                  />
                </div>{" "}
                <div className="form-group">
                  <label htmlFor="true" className="sr-only">
                    label
                  </label>
                  <select
                    className="form-control"
                    required="required"
                    type="text"
                    name='taskLevel'
                    value={taskLevel}
                    defaultValue={editingLevel}//1m02ss
                    onChange={this.handleChange}
                  >
                    <option value={0}>Small</option>
                    <option value={1}>Medium</option>
                    <option value={2}>High</option>
                  </select>
                </div>{" "}

                <button type="submit" className="btn btn-primary">
                  Submit
                </button> {" "}
                <button
                  type="button"
                  className="btn btn-default"
                  onClick={this.props.toggleForm}
                >Cancel
                </button>
              </form>
            }
          </div>
        </div >
        <br />
      </>
    );
  }
}

export default Form;
