import React, { Component } from "react";

class Sort extends Component {
  state = {
    orderBy: '',
    orderDir: ''
  };

  handleSortByName = () => {
    let orderDir = 'ASC'
    this.props.handleSort(this.state);
    this.updateOrderdir('NAME', 'ASC');
  }

  handleSortByLevel = () => {
    this.updateOrderdir();
    this.props.handleSort(this.state);
  }

  updateOrderdir = (value) => {
    if (value === 'NAME') {
      if (this.state.orderDir === ('ASC' || '')) {
        this.setState({
          orderBy: value,
          orderDir: 'DESC'
        });
      }
      else this.setState({
        orderBy: value,
        orderDir: 'ASC'
      });
    }

  }

  render() {
    const { orderDir, orderBy } = this.state;
    console.log(this.state);

    return (
      <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
        <div className="dropdown">
          <button
            type="button"
            className="btn btn-default dropdown-toggle"
            id="dropdownMenu1"
            data-toggle="dropdown"
            aria-haspopup="true"
          >
            Sort By
            <span className="caret" />
          </button>{" "}
          <ul className="dropdown-menu" aria-labelledby="dropdownMenu1">
            <li>
              <a
                role="button"
                href="##"
                type={orderBy}
                onClick={this.handleSortByName}
              > Name ASC/DESC </a>
            </li>
            <li role="separator" className="divider" href="##"></li>
            <li>
              <a
                role="button"
                href="##"
                onClick={this.handleSortByLevel}
              > Level ASC/DESC </a>
            </li>
          </ul>
          <span
            className="label label-success label-medium"
            style={{ fontSize: "14px" }}
          >
            {
              orderBy ? (`${orderBy} - ${orderDir}`) : ''
              // orderBy - orderDir // lỗi, v40xx1
            }
          </span>
        </div>
      </div >
    );
  }
}

export default Sort;
