import React, { Component } from "react";

//src7-item2
class Item extends Component {

  state = {
    edit: false,
    taskName: '',
    taskLevel: 0,
  };

  handleChange = (event) => {
    const { name, value } = event.target;
    console.log(name, value);
    this.setState({ [name]: value });
  }

  handleEditItem = () => {
    console.log(123);
    this.setState({ edit: true })
  }

  handleCancel = () => {
    this.setState({ edit: false })
  }

  showLevelEle = (level) => {
    let levelEle = null;
    let label = null;
    if (level === 0) {
      levelEle = "Small";
      label = "label label-primary";
    } else if (level === 1) {
      levelEle = "Medium";
      label = "label label-warning";
    } else if (level === 2) {
      levelEle = "High";
      label = "label label-info";
    }
    return <span className={label}>{levelEle}</span>;
  };

  render() {
    const { idxLabel, item, handleDelete } = this.props;
    let { edit, taskName, taskLevel } = this.state;
    console.log(this.state);

    if (edit) {
      taskName = item.name;
      taskLevel = item.level;
    }

    return (
      <>
        {
          edit ? (
            <div className="modal fade" id="modalTrigger" tabIndex={-1} >
              <div className="modal-dialog">
                <div className="modal-content">

                  <div className="modal-header">
                    <h3>Updating content
                      <button type="button" className="close" data-dismiss="modal" >
                        <span>X</span>
                      </button>
                    </h3>
                  </div>

                  <div className="modal-body">
                    <form action="true" method="post"
                      onSubmit={this.handleSubmit}
                    >
                      <div className="form-group">
                        <label htmlFor="taskName" className="">
                          <h4>Task name</h4>
                        </label>
                        <input
                          id="taskName"
                          type="text"
                          placeholder="Name"
                          className="form-control"
                          name='taskName'
                          value={taskName}
                          onChange={this.handleChange}
                        />
                      </div>{" "}

                      <div className="form-group">
                        <label htmlFor="true" className="">
                          <h4>Level</h4>
                        </label>
                        <select
                          className="form-control"
                          required="required"
                          type="text"
                          name='taskLevel'
                          value={taskLevel}
                          onChange={this.handleChange}
                        >
                          <option value={0}>Small</option>
                          <option value={1}>Medium</option>
                          <option value={2}>High</option>
                        </select>{" "}

                        <div className="modal-footer">
                          <button
                            type="submit"
                            className="btn btn-success"
                          > Update
                          </button>{" "}

                          <button
                            type="button"
                            className="btn btn-danger"
                            data-dismiss="modal"
                          >Cancel
                          </button>
                        </div>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>

          )
            : (<tr>
              <td className="text-center">{idxLabel}</td>
              <td>{item.name}</td>
              <td className="text-center">
                {this.showLevelEle(item.level)}
              </td>
              <td>
                {/* má»›i thay bth EDIT  */}
                <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#modalTrigger"
                  onClick={() => this.handleEditItem}
                >EDIT</button> {" "}

                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={() => handleDelete(item.id)}
                >Delete
                </button>
              </td>
            </tr>)
        }
      </>
    );
  }
}

export default Item;
