import React, { Component, createRef } from "react";

class Search extends Component {
  state = {
    strSearch: '',
  }

  searchRef = createRef(); // 3ms59ss

  // 2ms30ss
  handleChange = () => {
    console.log(this.searchRef);
    this.setState({ strSearch: this.searchRef.current })
    this.setState({ strSearch: this.searchRef.current })
    console.log(this.strSearch);
  }

  handleInput = (e) => {
    console.log(e.target.value);
    this.searchRef.current = e.target.value; // 3ms59ss
  }

  render() {

    return (
      <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Search for..."
            onChange={this.handleInput}
          />
          <span className="input-group-btn">
            <button
              type="button"
              className="btn btn-info"
              onClick={this.handleChange} //2ms30ss
            >
              Go!
            </button>
          </span>
        </div>
      </div>
    );
  }
}

export default Search;
