import "./App.css";
import React, { Component } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";
import List from "./components/List";
import items from "./mock/task";
import SearchSortHandler from "./control/SearchSortHandler";

class App extends Component {
  state = {
    items: items,
    isShowForm: false,
    strSearch: '',
    orderBy: '',
    orderDir: '',
  };

  handleToggleForm = () => {
    this.setState({ isShowForm: !this.state.isShowForm });
  };

  handleSearch = (value) => {
    this.setState({ strSearch: value });
  }

  handleSort = (value) => {
    this.setState({
      orderBy: value.orderBy,
      orderDir: value.orderDir
    })
  }

  handleDelete = (id) => {
    const items = this.state.items.filter(item => item.id !== id);
    this.setState({ items: items });
  }

  handleAdd = (value) => {
    const { items } = this.state;
    const { v4: uuidv4 } = require("uuid");
    items.push({
      id: uuidv4(),
      name: value.taskName,
      level: value.level,
    });
    this.setState({ items: items })
    console.log();
  }

  render() {
    const { items, isShowForm,
      strSearch, orderBy, orderDir } = this.state;

    if (strSearch || orderBy) {
      var listShown = SearchSortHandler(
        items, orderBy, orderDir, strSearch);
    }

    return (
      <div>
        <Title />
        <Control
          toggleForm={this.handleToggleForm}
          isShowForm={isShowForm}
          strSearch={strSearch}
          handleSearch={this.handleSearch}
          handleSort={this.handleSort}
        />
        {
          isShowForm ?
            <Form
              toggleForm={this.handleToggleForm}
              handleAdd={this.handleAdd}
            /> : ""
        }
        {
          (<List
            items={listShown ? listShown : items}
            handleDelete={this.handleDelete}
          />)
        }
      </div>
    );
  }
}

export default App;
