import "./App.css";
import React, { Component } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";
import List from "./components/List";
import items from "./mock/task";

//src4 - App2.js
class App extends Component {
  state = {
    items: items,
    isShowForm: false,
    strSearch: '',
  };

  handleToggleForm = () => {
    this.setState({ isShowForm: !this.state.isShowForm });
  };

  handleSearch = (value) => {
    this.setState({ strSearch: value });
  }

  render() {
    const { items, isShowForm, strSearch } = this.state;

    // v39xx1
    this.searchForLevel = (searchedLower) => {
      let searchedNum = null; //to num
      if ('small'.includes(searchedLower)) { searchedNum = 0; }
      else if ('medium'.includes(searchedLower)) { searchedNum = 1; }
      else if ('high'.includes(searchedLower)) { searchedNum = 2; }
      return searchedNum;
    }

    // filtering search by...
    this.searchedfilter = (stateItems, searchedLower, searchedNum) => {
      let searchedItem = stateItems.filter(item => {
        if (item.name.toLowerCase().includes( // task name
          searchedLower)) { return item; }
        else if (item.level === searchedNum) { return item }// level
        return null;
      })
      return searchedItem;
    }

    // v38xx2
    let listShown = null;
    if (strSearch) {
      //converting cases
      let searchedLower = strSearch.toLowerCase();//to lower
      let searchedNum = this.searchForLevel(searchedLower);

      //filtering search by...
      let searchedItem = this.searchedfilter(items, searchedLower, searchedNum)

      listShown = <List items={searchedItem} />
    }
    else {
      listShown = <List items={items} />
    }

    return (
      <div>
        <Title />
        <Control
          toggleForm={this.handleToggleForm}
          isShowForm={isShowForm}
          strSearch={strSearch}
          handleSearch={this.handleSearch}
        />
        {
          isShowForm ?
            <Form toggleForm={this.handleToggleForm} /> : ""
        }

        {/* v38xx2 */}
        {listShown}
        {/* <List items={items} /> */}
      </div>
    );
  }
}

export default App;
