import React, { Component } from "react";

//src7
class FormItem extends Component {
  state = {
    taskName: '',
    taskLevel: 0,
    edit: false,
  }

  handleChange = (event) => {
    console.log('handleChange');
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSubmit = (event) => {
    console.log('handleSubmit');
    event.preventDefault();
    this.props.handleAdd(this.state);
    this.setState({
      taskName: '',
      taskLevel: 0,
    });
  }

  handleCancel = () => {
    console.log('handleCancel');
    this.setState({ edit: false });
  }

  render() {
    const { taskName, taskLevel } = this.state;

    return (
      <>
        <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#modalTrigger">EDIT</button>

        <div className="modal fade" id="modalTrigger" tabIndex={-1} >
          <div className="modal-dialog">
            <div className="modal-content">

              <div className="modal-header">
                <h3>Updating content
                  <button type="button" className="close" data-dismiss="modal" >
                    <span>X</span>
                  </button>
                </h3>
              </div>

              <div className="modal-body">
                <form action="true" method="post"
                  onSubmit={this.handleSubmit}
                >
                  <div className="form-group">
                    <label htmlFor="taskName" className="">
                      <h4>Task name</h4>
                    </label>
                    <input
                      id="taskName"
                      type="text"
                      placeholder="Name"
                      className="form-control"
                      name='taskName'
                      value={taskName}
                      onChange={this.handleChange}
                    />
                  </div>{" "}

                  <div className="form-group">
                    <label htmlFor="true" className="">
                      <h4>Level</h4>
                    </label>
                    <select
                      className="form-control"
                      required="required"
                      type="text"
                      name='taskLevel'
                      value={taskLevel}
                      onChange={this.handleChange}
                    >
                      <option value={0}>Small</option>
                      <option value={1}>Medium</option>
                      <option value={2}>High</option>
                    </select>{" "}

                    <div className="modal-footer">
                      <button
                        type="submit"
                        className="btn btn-success"
                      > Update
                      </button>{" "}

                      <button
                        type="button"
                        className="btn btn-danger"
                        data-dismiss="modal"
                      >Cancel
                      </button>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
        <br />
      </>
    );
  }
}

export default FormItem;
