import "./App.css";
import React, { Component } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";
import List from "./components/List";
import items from "./mock/task";

//src4 - App3.js
class App extends Component {
  state = {
    items: items,
    isShowForm: false,
    strSearch: '',

    //1m22ss
    orderBy: '',
    orderDir: ''
  };

  handleToggleForm = () => {
    this.setState({ isShowForm: !this.state.isShowForm });
  };

  handleSearch = (value) => {
    this.setState({ strSearch: value });
  }

  handleSort = (value) => {
    console.log(value);
    // 1m22ss
    this.setState({
      orderBy: value.orderBy,
      orderDir: value.orderDir
    })
  }

  render() {
    const { items, isShowForm,
      strSearch, orderBy, orderDir } = this.state;

    this.searchForLevel = (searchedLower) => {
      let searchedLevel = null; //to num
      if ('small'.includes(searchedLower)) { searchedLevel = 0; }
      else if ('medium'.includes(searchedLower)) { searchedLevel = 1; }
      else if ('high'.includes(searchedLower)) { searchedLevel = 2; }
      return searchedLevel;
    }

    this.searchedFilter = (stateItems, searchedLower, searchedLevel) => {
      let searchedItem = stateItems.filter(item => {
        if (item.name.toLowerCase().includes(
          searchedLower)) { return item; }
        else if (item.level === searchedLevel) { return item }
        return null;
      });
      return searchedItem;
    }

    this.sortNameBy = (list, orderDir) => {
      if (orderDir === 'ASC') {
        return list.sort((a, b) => {
          const nameA = a.name.toUpperCase();
          const nameB = b.name.toUpperCase();
          if (nameA < nameB) return -1;
          else if (nameA > nameB) return 1;
          else return 0;
        });
      }
      else {
        return list.sort((a, b) => {
          const nameA = a.name.toUpperCase();
          const nameB = b.name.toUpperCase();
          if (nameA < nameB) return 1;
          else if (nameA > nameB) return -1;
          else return 0;
        });
      }
    }

    this.sortLevelBy = (list, orderDir) => {
      if (orderDir === 'ASC') {
        return list.sort((a, b) => {
          return a.value - b.value;
        });
      }
      else {
        return list.sort((a, b) => {
          return b.value - a.value;
        });
      }
    }

    this.handleSortByName = (list, orderDir) => {
      return this.sortNameBy(list, orderDir);
    }

    this.handleSortByLevel = (list, orderDir) => {
      let test = this.sortLevelBy(list, orderDir);
      console.log(test);
    }

    // v40xx2
    let listShown = null;
    if (strSearch) {
      let searchedLower = strSearch.toLowerCase();
      let searchedLevel = this.searchForLevel(searchedLower);
      listShown = this.searchedFilter(items, searchedLower, searchedLevel);
      if (orderBy === 'NAME') { //có orderBy -> sort 'listShown'
        listShown = this.handleSortByName(listShown, orderDir);
      }
      else {
        listShown = this.handleSortByLevel(listShown, orderDir);
      }
    }
    else if (!strSearch) {
      //có orderBy -> sort 'items'
      if (orderBy === 'NAME') {
        if (orderBy) listShown = this.handleSortByName(items, orderDir);
      }
      else {

      }
    }

    return (
      <div>
        <Title />
        <Control
          toggleForm={this.handleToggleForm}
          isShowForm={isShowForm}
          strSearch={strSearch}
          handleSearch={this.handleSearch}
          handleSort={this.handleSort}
        />
        {
          isShowForm ?
            <Form toggleForm={this.handleToggleForm} /> : ""
        }
        {
          // v40xx2
          listShown ?
            <List items={listShown} /> : <List items={items} />
        }
      </div>
    );
  }
}

export default App;
