import "./App.css";
import React, { Component } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";
import List from "./components/List";
import items from "./mock/task";

//src4 - App2.js
class App extends Component {
  state = {
    items: items,
    isShowForm: false,
    strSearch: "",
  };

  handleToggleForm = () => {
    this.setState({ isShowForm: !this.state.isShowForm });
  };

  handleSearch = (value) => {
    this.setState({ strSearch: value });
  };

  render() {
    const { items, isShowForm, strSearch } = this.state;

    // v39xx1
    this.searchForLevel = (searchedLower) => {
      let searchedLevel = null; //to num
      if ("small".includes(searchedLower)) {
        searchedLevel = 0;
      } else if ("medium".includes(searchedLower)) {
        searchedLevel = 1;
      } else if ("high".includes(searchedLower)) {
        searchedLevel = 2;
      }
      return searchedLevel;
    };

    // filtering search by task name + level, v39xx1
    this.searchedFilter = (stateItems, searchedLower, searchedLevel) => {
      let searchedItem = stateItems.filter((item) => {
        if (item.name.toLowerCase().includes(searchedLower)) {
          return item;
        } else if (item.level === searchedLevel) {
          return item;
        }
        return null;
      });
      return searchedItem;
    };

    // v39xx1
    let searchedItem = null;
    if (strSearch) {
      //converting searched cases
      let searchedLower = strSearch.toLowerCase(); //to lower
      let searchedLevel = this.searchForLevel(searchedLower);

      //filtering search by...
      searchedItem = this.searchedFilter(items, searchedLower, searchedLevel);
    }

    return (
      <div>
        <Title />
        <Control
          toggleForm={this.handleToggleForm}
          isShowForm={isShowForm}
          strSearch={strSearch}
          handleSearch={this.handleSearch}
        />
        {isShowForm ? <Form toggleForm={this.handleToggleForm} /> : ""}

        {
          //v39xx1
          searchedItem ? <List items={searchedItem} /> : <List items={items} />
        }
      </div>
    );
  }
}

export default App;
