import React, { Component } from "react";

//src7-item2
class Item extends Component {

  state = {
    edit: false,
    taskName: '',
    taskLevel: 0,
  };

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleEdit = () => {
    console.log('handleEditItem');
    this.setState({ edit: true })
  }

  handleCancel = () => {
    console.log('handleCancel');
    this.setState({ edit: false })
  }

  handleSubmit = () => {
    console.log('handleSubmit');
  }

  showLevelEle = (level) => {
    let levelEle = null;
    let label = null;
    if (level === 0) {
      levelEle = "Small";
      label = "label label-primary";
    } else if (level === 1) {
      levelEle = "Medium";
      label = "label label-warning";
    } else if (level === 2) {
      levelEle = "High";
      label = "label label-info";
    }
    return <span className={label}>{levelEle}</span>;
  };

  render() {
    const { idxLabel, item, handleDelete, handleEdit } = this.props;
    let { edit, taskName, taskLevel } = this.state;

    return (
      <>
        {
          (<tr>
            <td className="text-center">{idxLabel}</td>
            <td>{item.name}</td>
            <td className="text-center">
              {this.showLevelEle(item.level)}
            </td>
            <td>
              {/* má»›i thay btn EDIT  */}
              <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#modalTrigger"
                onClick={this.handleEdit}
              >EDIT</button> {" "}

              <button
                type="button"
                className="btn btn-danger"
                onClick={() => handleDelete(item.id)}
              >Delete
              </button>
            </td>
          </tr>)
        }
      </>
    );
  }
}

export default Item;
