import React, { Component } from "react";

//src7
class Form extends Component {
  state = {
    // v49xx2
    taskName: '',
    taskLevel: 0,
    edit: false,
  }

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSubmit = (event) => {
    event.preventDefault();

    /*cach 1*/
    // const { taskName, taskLevel } = this.state;
    // this.props.handleAdd({
    //   taskName: taskName,
    //   taskLevel: taskLevel
    // });

    /*cach 2*/
    this.props.handleAdd(this.state);

    // v49xx4
    this.setState({
      taskName: '',
      taskLevel: 0,
    });
  }

  handleEdit = () => {
    const { edit } = this.state;
    const { editingItem } = this.props;

    this.setState({
      edit: !edit,
      taskName: editingItem.name,
      taskLevel: editingItem.level
    })
  }

  render() {
    let { taskName, taskLevel, edit } = this.state;
    let { editingItem } = this.props;
    if (editingItem && edit === false) {
      {
        editingItem && edit === false ? (this.handleEdit()) : null
      }
      console.log(this.state);
    }

    return (
      <>
        <div className="row">
          <div className="col-md-offset-7 col-md-5">
            <form
              action="true" method="post" className="form-inline"
              onSubmit={this.handleSubmit} //v49xx2
            >
              <div className="form-group">
                <label htmlFor="true" className="sr-only">
                  label
                </label>
                <input
                  type="text"
                  placeholder="Name"
                  className="form-control"

                  // v49xx2
                  name='taskName'
                  value={taskName}
                  onChange={this.handleChange}
                />
              </div>{" "}
              <div className="form-group">
                <label htmlFor="true" className="sr-only">
                  label
                </label>
                <select
                  className="form-control"
                  required="required"

                  //v49xx1
                  type="text"
                  // type="number"

                  // v49xx2
                  name='taskLevel'
                  value={taskLevel}
                  onChange={this.handleChange}
                >
                  <option value={0}>Small</option>
                  <option value={1}>Medium</option>
                  <option value={2}>High</option>
                </select>
              </div>{" "}

              {
                edit ? (
                  <button type="submit" className="btn btn-primary">
                    Update
                  </button>)
                  : (<button type="submit" className="btn btn-primary">
                    Submit
                  </button>)
              }

              {" "}
              <button
                type="button"
                className="btn btn-default"
                onClick={this.props.toggleForm}  // 11ss
              >Cancel
              </button>
            </form>
          </div>
        </div >
        <br />        
      </>
    );
  }
}

export default Form;
