//src1, 9ms25ss
import React, { Component } from "react";
import { ThemeContext } from "../context/ThemeContextProvider";

class ThemeChange extends Component {
  static contextType = ThemeContext; // 9ms35ss

  render() {
    // 9ms51ss
    const { theme, changeTheme } = this.context;
    const color = theme === "day" ? "yellow" : "black";

    return (
      <div>
        <h1 style={{ height: "300px", background: color }}>
          Welcome to React context api
        </h1>
        <button onClick={changeTheme}>change theme</button>
      </div>
    );
  }
}

export default ThemeChange;
