import React, { Component } from "react";

//src7
class FormItem extends Component {
  state = {
    taskName: '',
    taskLevel: 0,
    edit: false,
  }

  handleChange = (event) => {
    console.log('handleChange');
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSubmit = (event) => {
    console.log('handleSubmit');
    event.preventDefault();
    this.props.handleAdd(this.state);
    this.setState({
      taskName: '',
      taskLevel: 0,
    });
  }

  handleCancel = () => {
    console.log('handleCancel');
    this.setState({ edit: false });
  }

  render() {
    const { taskName, taskLevel } = this.state;

    return (
      <>
        <div className="row">
          <div style={{ margin: '200px' }}>
            <div>
              <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModal" data-whatever="@mdo">EDIT</button>

              <div className="modal fade" id="exampleModal" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog" role="document">
                  <div className="modal-content">
                    <div className="modal-header">
                      <h4 className="modal-title" id="exampleModalLabel">Updating content</h4>
                      <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">Ã—</span>
                      </button>
                    </div>
                    <div className="modal-body">
                      <form
                        action="true" method="post"
                        onSubmit={this.handleSubmit}
                      >
                        <div className="form-group">
                          <label htmlFor="true" className="sr-only">
                            label
                          </label>
                          <input
                            type="text"
                            placeholder="Name"
                            className="form-control"
                            name='taskName'
                            value={taskName}
                            onChange={this.handleChange}
                          />
                        </div>{" "}
                        <div className="form-group form-inline">
                          <label htmlFor="true" className="sr-only">
                            label
                          </label>
                          <select
                            className="form-control"
                            required="required"
                            type="text"
                            name='taskLevel'
                            value={taskLevel}
                            onChange={this.handleChange}
                          >
                            <option value={0}>Small</option>
                            <option value={1}>Medium</option>
                            <option value={2}>High</option>
                          </select>{" "}

                          <button type="submit" className="btn btn-primary">
                            Submit
                          </button>{" "}
                          <button
                            type="button"
                            className="btn btn-default"
                            onClick={this.handleCancel}
                          >Cancel
                          </button>
                        </div>
                      </form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div >
        <br />
      </>
    );
  }
}

export default FormItem;
