import React, { Component } from "react";

//src7-item2
class Item extends Component {

  state = {
    edit: false,
  };

  handleEdit = () => {
    this.setState({ edit: true })
  }

  handleCancel = () => {
    this.setState({ edit: false })
  }

  showLevelEle = (level) => {
    let levelEle = null;
    let label = null;
    if (level === 0) {
      levelEle = "Small";
      label = "label label-primary";
    } else if (level === 1) {
      levelEle = "Medium";
      label = "label label-warning";
    } else if (level === 2) {
      levelEle = "High";
      label = "label label-info";
    }
    return <span className={label}>{levelEle}</span>;
  };

  render() {
    const { idxLabel, item, handleDelete } = this.props;
    const { edit } = this.state;

    return (
      <>
        {
          edit ? (
            <tr>
              <td className="text-center">{idxLabel}</td>
              <td>{item.name + " EDIT________"}</td>
              <td className="text-center">
                {this.showLevelEle(item.level)}
              </td>
              <td>
                <button
                  type="button"
                  className="btn btn-success"
                  onClick={() => this.handleEdit(item.id)}
                > Edit
                </button>
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={this.handleCancel}
                >Cancel
                </button>
              </td>
            </tr>)
            : (<tr>
              <td className="text-center">{idxLabel}</td>
              <td>{item.name}</td>
              <td className="text-center">
                {this.showLevelEle(item.level)}
              </td>
              <td>
                {/* má»›i thay */}
                <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#modalTrigger"
                  onClick={() => this.handleEdit(item.id)}
                >EDIT</button>

                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={() => handleDelete(item.id)}
                >Delete
                </button>
              </td>
            </tr>)
        }
      </>
    );
  }
}

export default Item;
