import "./App.css";
import React, { Component } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";
import List from "./components/List";
import items from "./mock/task";

class App extends Component {
  state = {
    items: items,
    isShowForm: false,
    strSearch: '',
  };

  handleToggleForm = () => {
    this.setState({ isShowForm: !this.state.isShowForm });
  };

  handleSearch = (value) => {
    this.setState({ strSearch: value });
  }


  render() {
    const { items, isShowForm, strSearch } = this.state;
    // console.log('strSearch:', strSearch);


    // v38xx2
    let listShown = null;
    if (strSearch) {
      //converting into lower case
      let lowerSearch = strSearch.toLowerCase();
      let itemSearch = null;

      //converting to number
      if (lowerSearch.includes('small')) { lowerSearch = 0; }
      else if (lowerSearch.includes('medium')) { lowerSearch = 1; }
      else if (lowerSearch.includes('high')) { lowerSearch = 2; }

      //filtering search by task name
      if (typeof (lowerSearch) === 'number') {
        itemSearch = items.filter(item => {
          if (item.level === lowerSearch) { return item }
          return false;
        })
      }

      //filtering search by level
      if (typeof (lowerSearch) === 'string') {
        itemSearch = items.filter(item => {
          if (item.name.toLowerCase().includes(
            lowerSearch)) { return item; }
          return false;
        })
      }
      listShown = <List items={itemSearch} />
    }
    else {
      listShown = <List items={items} />
    }


    return (
      <div>
        <Title />
        <Control
          toggleForm={this.handleToggleForm}
          isShowForm={isShowForm}
          strSearch={strSearch}
          handleSearch={this.handleSearch}
        />
        {
          isShowForm ?
            <Form toggleForm={this.handleToggleForm} /> : ""
        }

        {/* v38xx2 */}
        {listShown}
        {/* <List items={items} /> */}
      </div>
    );
  }
}

export default App;
