import React, { Component } from "react";

//src7-item2
class Item extends Component {

  state = {
    edit: false,
  };

  handleEdit = (id) => {
    this.setState({ edit: true })
  }

  showLevelEle = (level) => {
    let levelEle = null;
    let label = null;
    if (level === 0) {
      levelEle = "Small";
      label = "label label-primary";
    } else if (level === 1) {
      levelEle = "Medium";
      label = "label label-warning";
    } else if (level === 2) {
      levelEle = "High";
      label = "label label-info";
    }
    return <span className={label}>{levelEle}</span>;
  };

  render() {
    const { idxLabel, item, handleDelete } = this.props;
    const { edit } = this.state;

    return (
      <>
        {
          edit ? (<tr>
            <td className="text-center">{idxLabel}</td>
            <td>{item.name = 'EDIT'}</td>
            <td className="text-center">
              {this.showLevelEle(item.level)}
            </td>
            <td>
              <button
                type="button"
                className="btn btn-suc"
                onClick={() => this.handleEdit(item.id)}
              > Subm
              </button>
              <button
                type="button"
                className="btn btn-danger"
                onClick={() => handleDelete(item.id)}
              >Cancel
              </button>
            </td>
          </tr>)

            : (<tr>
              <td className="text-center">{idxLabel}</td>
              <td>{item.name}</td>
              <td className="text-center">
                {this.showLevelEle(item.level)}
              </td>
              <td>
                <button
                  type="button"
                  className="btn btn-warning"
                  onClick={() => this.handleEdit(item.id)}
                >Edit
                </button>
                <button
                  type="button"
                  className="btn btn-danger"
                  onClick={() => handleDelete(item.id)}
                >Delete
                </button>
              </td>
            </tr>)
        }
      </>
    );
  }
}

export default Item;
