import React, { Component } from "react";

class Sort extends Component {
  state = {
    orderBy: 'NAME',
    orderDir: 'ASC'
  };

  handleSortByName = () => {
    this.props.handleSort(this.state);

    if (this.state.orderDir === 'ASC') {
      this.setState({ orderDir: 'DESC' });
    }
    else this.setState({ orderDir: 'ASC' });
  }

  handleSortByLevel = (e) => {
    this.props.handleSort(this.state);
    updateOrderdir()
  }

  updateOrderdir = () => {
    if (this.state.orderDir === 'ASC') {
      this.setState({ orderDir: 'DESC' });
    }
    else this.setState({ orderDir: 'ASC' });
  }

  render() {
    const { orderDir, orderBy } = this.state;
    console.log(this.state);

    return (
      <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
        <div className="dropdown">
          <button
            type="button"
            className="btn btn-default dropdown-toggle"
            id="dropdownMenu1"
            data-toggle="dropdown"
            aria-haspopup="true"
          >
            Sort By
            <span className="caret" />
          </button>{" "}
          <ul className="dropdown-menu" aria-labelledby="dropdownMenu1">
            <li>
              <a
                role="button"
                href="##"
                name={orderBy}
                onClick={this.handleSortByName}
              > Name ASC/DESC </a>
            </li>
            <li role="separator" className="divider" href="##"></li>
            <li>
              <a role="button" href="##">
                Level ASC/DESC
              </a>
            </li>
          </ul>
          <span
            className="label label-success label-medium"
            style={{ fontSize: "14px" }}
          >
            {
              `${orderBy} - ${orderDir}`
              // orderBy - orderDir // lỗi, v40xx1
            }
          </span>
        </div>
      </div >
    );
  }
}

export default Sort;
