import "./App.css";
import React, { Component } from "react";
import Title from "./components/Title";
import Control from "./components/Control";
import Form from "./components/Form";
import List from "./components/List";
import items from "./mock/task";

//src3
class App extends Component {
  state = {
    items: items,
    isShowForm: false, // 46ss
    strSearch: '', //48ss
  };

  // 3ms13ss
  handleToggleForm = () => {
    this.setState({ isShowForm: !this.state.isShowForm });
  };

  // 12ss
  handleSearch = (value) => {
    console.log(value);
  }

  render() {
    const { items, isShowForm, strSearch } = this.state; // 46ss

    return (
      <div>
        <Title />
        {/* 3ms13ss */}
        <Control
          toggleForm={this.handleToggleForm}
          isShowForm={isShowForm} //1m49ss
          strSearch={strSearch} //48ss
          handleSearch={this.handleSearch} //12ss
        />
        {// 46ss, 11ss
          isShowForm ?
            <Form toggleForm={this.handleToggleForm} /> : ""
        }

        <List items={items} />
      </div>
    );
  }
}

export default App;
