import React, { Component } from "react";

class Sort extends Component {
  state = {
    orderByUi: '',
    orderDirUi: '',
    orderBy: '',
    // v41xx7
    orderDirName: 'ASC',
    orderDirLevel: 'ASC',
  };

  handleSortByName = (e) => {
    let { orderDirName } = this.state;

    this.props.handleSort({
      orderBy: e.target.type, //v40xx3
      orderDir: orderDirName,
    });

    this.setState({
      // v40xx6
      orderByUi: e.target.type,
      orderDirUi: orderDirName,
      orderBy: e.target.type,
      orderDirName: orderDirName === 'ASC' ? 'DESC' : 'ASC' // v41xx7
    })
  }

  handleSortByLevel = (e) => {
    let { orderDirLevel } = this.state;

    this.props.handleSort({
      orderBy: e.target.type,
      orderDir: orderDirLevel,
    });

    this.setState({
      orderByUi: e.target.type,
      orderDirUi: orderDirLevel,
      orderBy: e.target.type,
      orderDirLevel: orderDirLevel === 'ASC' ? 'DESC' : 'ASC' // v41xx7
    })
  }

  handleSortByNone = (e) => {
    this.props.handleSort({
      orderBy: e.target.type,
      orderDir: '',
    });

    this.setState({
      orderByUi: e.target.type,
      orderBy: e.target.type,

      //v41xx6, v41xx7
      orderDirName: 'ASC',
      orderDirLevel: 'ASC',
    })
  }

  render() {
    const { orderByUi, orderDirUi } = this.state;

    return (
      <div className="col-xs-3 col-sm-3 col-md-3 col-lg-3">
        <div className="dropdown">
          <button
            type="button"
            className="btn btn-default dropdown-toggle"
            id="dropdownMenu1"
            data-toggle="dropdown"
            aria-haspopup="true"
          >
            Sort By
            <span className="caret" />
          </button>{" "}
          <ul className="dropdown-menu" aria-labelledby="dropdownMenu1">
            <li>
              <a
                role="button"
                href="##"
                type="NAME"
                onClick={this.handleSortByName} //v40xx3
              > Name ASC/DESC </a>
            </li>
            <li role="separator" className="divider" href="##"></li>
            <li>
              <a
                role="button"
                href="##"
                type="LEVEL"
                onClick={this.handleSortByLevel} //v41xx1
              > Level ASC/DESC </a>
            </li>
            <li role="separator" className="divider" href="##"></li>
            <li>
              <a
                role="button"
                href="##"
                type=""
                onClick={this.handleSortByNone} //v41xx1
              > None </a>
            </li>
          </ul>
          <span
            className="label label-success label-medium"
            style={{ fontSize: "14px" }}
          >
            {
              // v40xx6, v40xx1
              orderByUi ? (`${orderByUi} - ${orderDirUi}`) : ('')
              // orderBy - orderDir // lỗi, 
            }
          </span>
        </div>
      </div >
    );
  }
}

export default Sort;
